-- 카테고리: name 유니크
CREATE UNIQUE INDEX IF NOT EXISTS uq_category_name
    ON category (name);

-- 서브카테고리: (category_id, name) 복합 유니크
CREATE UNIQUE INDEX IF NOT EXISTS uq_sub_category_catid_name
    ON sub_category (category_id, name);



